import React, { useState } from 'react'
import Note from './components/Note'

const App = (props) => {
  const [notes, setNotes] = useState(props.notes)

  const rows = () => notes.map(note =>
    <Note
      key={note.id}
      note={note}
    />
  )

  const addNote = (event) => {
      event.preventDefault()
      console.log("Button clicked", event.target)
  }

  return (
    <div>
      <h1>Notes</h1>
      <ul>
        {rows()}
      </ul>
    </div>
  )
}

export default App
